#include <vector>
#include <set>
#include <algorithm>
#include <iostream>
#include <map>

using namespace std;

void print(const vector<int>& v)
{
  vector<int>::const_iterator it;
  for (it=v.begin(); it!=v.end(); ++it)
    cout << *it << " ";
  cout << endl;
}

class Solution {
public:
  vector<vector<int> > threeSum(vector<int> &num) {
    map<int, int> m;
    for (size_t i=0; i<num.size(); i++)
      m[num[i]]++;
    map<int, int>::iterator it;
    set<vector<int> > s;
    for (it=m.begin(); it!=m.end(); ++it) {
      if (it->first==0) {
        if (it->second >= 3) {
          vector<int> v(3, 0);
          s.insert(v);
          it->second = 1;
        }
      } else if (it->second >= 2) {
        int n = it->first;
        if (m.find(-n*2) != m.end()) {
          int arr[] = {n,n,-n*2};
          vector<int> v(arr, arr+sizeof(arr)/sizeof(arr[0]));
          sort(v.begin(), v.end());
          s.insert(v);
          it->second = 1;
        }
      }
    }

    vector<int> newnum;
    for (it=m.begin(); it!=m.end(); ++it) 
      newnum.push_back(it->first);

    sort(newnum.begin(), newnum.end());
    for (size_t i=0; i<newnum.size(); i++) {
      int first = newnum[i];
      vector<int> sub(newnum.begin()+i+1, newnum.end());
      set<vector<int> > tmp = twoSum(sub, -first);
      set<vector<int> >::iterator it;
      for (it=tmp.begin(); it!=tmp.end(); ++it)
        s.insert(*it);
    }
    vector<vector<int> > ret(s.size());
    copy(s.begin(), s.end(), ret.begin());
    return ret;
  }
private:
  set<vector<int> > twoSum(vector<int> &v, int n) {
    set<vector<int> > s;
    if (v.size() == 0)
      return s;
    size_t i = 0;
    size_t j = v.size()-1;
    while (i<j) {
      if (v[i]+v[j] < n)
        i++;
      else if (v[i]+v[j] > n)
        j--;
      else {
        int arr[] = {-n, v[i], v[j]};
        vector<int> tmp(arr, arr+sizeof(arr)/sizeof(arr[0]));
        s.insert(tmp);
        i++;
      }
    }
    return s;
  }
};

