#include <vector>
#include <algorithm>
#include <iostream>
#include <map>

using namespace std;

void print(const vector<int>& v)
{
  vector<int>::const_iterator it;
  for (it=v.begin(); it!=v.end(); ++it)
    cout << *it << " ";
  cout << endl;
}

class Solution {
public:
  vector<vector<int> > threeSum(vector<int> &num) {
    map<int, int> m;
    for (size_t i=0; i<num.size(); i++)
      m[num[i]]++;
    map<int, int>::iterator it;
    vector<vector<int> > ret;
    vector<int> newnum;
    for (it=m.begin(); it!=m.end(); ++it) {
      newnum.push_back(it->first);
      if (it->first==0) {
        if (it->second >= 3) {
          vector<int> v(3, 0);
          ret.push_back(v);
          it->second = 1;
        }
      } else if (it->second >= 2) {
        int n = it->first;
        if (m.find(-n*2) != m.end()) {
          int arr[] = {n,n,-n*2};
          vector<int> v(arr, arr+sizeof(arr)/sizeof(arr[0]));
          sort(v.begin(), v.end());
          ret.push_back(v);
          it->second = 1;
        }
      }
    }

    for (size_t i=0; i<newnum.size(); i++) {
      int first = newnum[i];
      int j = i+1;
      int k = int(newnum.size())-1;
      while (j < k) {
        if (newnum[j]+newnum[k] < -first)
          j++;
        else if (newnum[j]+newnum[k] > -first)
          k--;
        else {
          int arr[] = {first, newnum[j], newnum[k]};
          vector<int> tmp(arr, arr+sizeof(arr)/sizeof(arr[0]));
          ret.push_back(tmp);
          j++;
        }
      }
    }
    return ret;
  }
};

